mapper.select:

select 
*
from T_USER_FROZENACCOUNT
where FRONTID = #{frontID,jdbcType=DECIMAL}
and SESSIONID = #{sessionID,jdbcType=DECIMAL}
and ORDERREF = #{orderRef,jdbcType=VARCHAR}
and USER_NAME = #{subAccount,jdbcType=VARCHAR}

mapper.update:

update T_USER_FROZENACCOUNT set OFFSETFLAG = '2' , FROZEN_FLAG = '1'
where FRONTID = #{frontID,jdbcType=DECIMAL}
and SESSIONID = #{sessionID,jdbcType=DECIMAL}
and ORDERREF = #{orderRef,jdbcType=VARCHAR}
and USER_NAME = #{subAccount,jdbcType=VARCHAR}


update T_USER_FROZENACCOUNT set OFFSETFLAG = '2' , FROZEN_FLAG = '2'
,FROZENMARGIN = FROZENMARGIN - (FROZENMARGIN / VOLUMETOTALORIGINAL * #{volume,jdbcType=DECIMAL})
,FROZENCOMMISSION = FROZENCOMMISSION - (FROZENCOMMISSION / VOLUMETOTALORIGINAL * #{volume,jdbcType=DECIMAL})
,VOLUMETOTALORIGINAL = VOLUMETOTALORIGINAL - #{volume,jdbcType=DECIMAL}
where FRONTID = #{frontID,jdbcType=DECIMAL}
and SESSIONID = #{sessionID,jdbcType=DECIMAL}
and ORDERREF = #{orderRef,jdbcType=VARCHAR}
and USER_NAME = #{subAccount,jdbcType=VARCHAR}


update T_USER_FROZENACCOUNT set OFFSETFLAG = '1' , FROZEN_FLAG = '1'
where FRONTID = #{frontID,jdbcType=DECIMAL}
and SESSIONID = #{sessionID,jdbcType=DECIMAL}
and ORDERREF = #{orderRef,jdbcType=VARCHAR}
and USER_NAME = #{subAccount,jdbcType=VARCHAR}


update T_USER_FROZENACCOUNT set OFFSETFLAG = '1' , FROZEN_FLAG = '2'
,FROZENMARGIN = FROZENMARGIN - (FROZENMARGIN / VOLUMETOTALORIGINAL * #{volume,jdbcType=DECIMAL})
,FROZENCOMMISSION = FROZENCOMMISSION - (FROZENCOMMISSION / VOLUMETOTALORIGINAL * #{volume,jdbcType=DECIMAL})
,VOLUMETOTALORIGINAL = VOLUMETOTALORIGINAL - #{volume,jdbcType=DECIMAL}
where FRONTID = #{frontID,jdbcType=DECIMAL}
and SESSIONID = #{sessionID,jdbcType=DECIMAL}
and ORDERREF = #{orderRef,jdbcType=VARCHAR}
and USER_NAME = #{subAccount,jdbcType=VARCHAR}
